import React, { useState } from 'react';
import styles from './Todo.module.css';
import AddModal from './AddModal';

function Todo() {
    const [todos, setTodos] = useState([
        { id: 1, title: 'ÏπúÍµ¨ ÏÉùÏùºÏÑ†Î¨º ÏÇ¨Í∏∞', date: '2024-11-20', label: 'ÏÑ†Î¨º', priority: 'Ï§ëÏöî', status: false },
        { id: 2, title: 'Í≥ºÏ†úÌïòÍ∏∞', date: '2024-11-15', label: 'Í≥µÎ∂Ä', priority: 'ÏùºÎ∞ò', status: false }
    ]);

    const [showModal, setShowModal] = useState(false);

    // Ìï† Ïùº Ï∂îÍ∞Ä Ìï®Ïàò (Î™®Îã¨ÏóêÏÑú Ï†ÑÎã¨Îêú Îç∞Ïù¥ÌÑ∞Î•º Î™©Î°ùÏóê Ï∂îÍ∞Ä)
    const addTodo = (newTodo) => {
        const todoItem = {
            id: Date.now(),
            ...newTodo,
            status: false // Ï¥àÍ∏∞ ÏÉÅÌÉúÎäî ÎØ∏ÏôÑÎ£åÎ°ú ÏÑ§Ï†ï
        };
        setTodos([...todos, todoItem]);
        setShowModal(false);
    };

    // Ìï† Ïùº ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ìï®Ïàò (Ï≤¥ÌÅ¨Î∞ïÏä§Î°ú ÏôÑÎ£å Ïó¨Î∂Ä Î≥ÄÍ≤Ω)
    const updateStatus = (id) => {
        setTodos(todos.map(todo => (todo.id === id ? { ...todo, status: !todo.status } : todo)));
    };

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Ìï† Ïùº Î™©Î°ù</h2>
            <ul className={styles.todoList}>
                {todos.map(todo => (
                    <li key={todo.id} className={styles.todoItem}>
                        <label className={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                checked={todo.status}
                                onChange={() => updateStatus(todo.id)}
                            />
                            <span className={styles.customCheckbox}></span>
                        </label>
                        <div className={styles.todoDetails}>
                            <span className={todo.status ? styles.completed : ''}>{todo.title}</span>
                            <div className={styles.metadata}>
                                {todo.date && <span className={styles.dueDate}>üìÖ {todo.date}</span>}
                                {todo.label && <span className={styles.label}>üè∑Ô∏è {todo.label}</span>}
                                {todo.priority && <span className={styles.priority}>‚ö° {todo.priority}</span>}
                            </div>
                        </div>
                    </li>
                ))}
            </ul>

            {/* Ìï† Ïùº Ï∂îÍ∞Ä Î™®Îã¨ Ïó¥Í∏∞ Î≤ÑÌäº */}
            <button
                onClick={() => setShowModal(true)}
                className={styles.addButton}
            >
                Ìï† Ïùº Ï∂îÍ∞ÄÌïòÍ∏∞
            </button>

            {/* Î™®Îã¨ Ïª¥Ìè¨ÎÑåÌä∏ */}
            <AddModal
                isOpen={showModal}
                onRequestClose={() => setShowModal(false)}
                onSave={addTodo}
            />
        </div>
    );
}

export default Todo;
