import React, { useState } from 'react';
import styles from './Todo.module.css';

function Todo() {
    const [todos, setTodos] = useState([
        { id: 1, text: 'ÏπúÍµ¨ ÏÉùÏùºÏÑ†Î¨º ÏÇ¨Í∏∞', status: false, dueDate: '2024/11/20', repeat: false, label: 'ÏÑ†Î¨º' },
        { id: 2, text: 'Í≥ºÏ†úÌïòÍ∏∞', status: false, dueDate: '2024/11/15', repeat: false, label: 'Í≥µÎ∂Ä' },
        { id: 3, text: 'ÎÖ∏Ìä∏ Ï≤¥ÌÅ¨ÌïòÍ∏∞', status: true, dueDate: '2024/11/14', repeat: true, label: 'Í≥µÎ∂Ä' },
        { id: 4, text: 'Ïì∞Î†àÍ∏∞ Î≤ÑÎ¶¨Í∏∞', status: true, dueDate: '2024/11/13', repeat: false, label: 'ÏßëÏïàÏùº' }
    ]);

    const [newTodo, setNewTodo] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [repeat, setRepeat] = useState(false);
    const [label, setLabel] = useState('');

    const addTodo = () => {
        if (newTodo.trim()) {
            const newTodoItem = {
                id: todos.length + 1,
                text: newTodo,
                status: false,
                dueDate: dueDate || 'ÎØ∏Ï†ï',
                repeat: repeat,
                label: label || 'Í∏∞ÌÉÄ'
            };
            setTodos([...todos, newTodoItem]);
            setNewTodo('');
            setDueDate('');
            setRepeat(false);
            setLabel('');
        }
    };

    const updateStatus = (id) => {
        setTodos(todos.map(todo =>
            todo.id === id ? { ...todo, status: !todo.status } : todo
        ));
    };

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Ìï† Ïùº Î™©Î°ù</h2>
            <ul className={styles.todoList}>
                {todos.map(todo => (
                    <li key={todo.id} className={styles.todoItem}>
                        <label className={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                checked={todo.status}
                                onChange={() => updateStatus(todo.id)}
                            />
                            <span className={styles.customCheckbox}></span>
                        </label>
                        <span className={todo.status ? styles.completed : ''}>
                            {todo.text}
                        </span>
                        {todo.repeat && <span className={styles.repeatIcon}>üîÑ</span>}
                        <span className={styles.label}>{todo.label}</span>
                        {todo.dueDate && <span className={styles.dueDate}>{todo.dueDate}</span>}
                    </li>
                ))}
            </ul>
            <div className={styles.inputContainer}>
                <input
                    type="text"
                    placeholder="Ìï† Ïùº Ï∂îÍ∞ÄÌïòÍ∏∞..."
                    value={newTodo}
                    onChange={(e) => setNewTodo(e.target.value)}
                    className={styles.input}
                />
                <input
                    type="date"
                    value={dueDate}
                    onChange={(e) => setDueDate(e.target.value)}
                    className={styles.input}
                />
                <input
                    type="text"
                    placeholder="ÎùºÎ≤® (Ïòà: Í≥µÎ∂Ä, ÏßëÏïàÏùº)"
                    value={label}
                    onChange={(e) => setLabel(e.target.value)}
                    className={styles.input}
                />
                <label className={styles.repeatCheckbox}>
                    Î∞òÎ≥µ
                    <input
                        type="checkbox"
                        checked={repeat}
                        onChange={(e) => setRepeat(e.target.checked)}
                    />
                </label>
                <button onClick={addTodo} className={styles.addButton}>
                    Ï∂îÍ∞Ä
                </button>
            </div>
        </div>
    );
}

export default Todo;
