import React, { useState } from 'react';
import styles from './Todo.module.css';

function Todo() {
    const [todos, setTodos] = useState([
        { id: 1, text: '친구 생일선물 사기', status: false },
        { id: 2, text: '과제하기', status: false },
        { id: 3, text: '노트 체크하기', status: true },
        { id: 4, text: '쓰레기 버리기', status: true }
    ]);

    const [newTodo, setNewTodo] = useState('');

    const addTodo = () => {
        if (newTodo.trim()) {
            const newTodoItem = {
                id: todos.length + 1,
                text: newTodo,
                status: false
            };
            setTodos([...todos, newTodoItem]);
            setNewTodo('');
        }
    };

    const updateStatus = (id) => {
        setTodos(todos.map(todo =>
            todo.id === id ? { ...todo, status: !todo.status } : todo
        ));
    };

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>할 일 목록</h2>
            <ul className={styles.todoList}>
                {todos.map(todo => (
                    <li key={todo.id} className={styles.todoItem}>
                        <label className={styles.checkboxContainer}>
                            <input
                                type="checkbox"
                                checked={todo.status}
                                onChange={() => updateStatus(todo.id)}
                            />
                            <span className={styles.customCheckbox}></span>
                            <span className={todo.status ? styles.completed : ''}>
                                {todo.text}
                            </span>
                        </label>
                    </li>
                ))}
            </ul>
            <div className={styles.inputContainer}>
                <input
                    type="text"
                    placeholder="할 일 추가하기..."
                    value={newTodo}
                    onChange={(e) => setNewTodo(e.target.value)}
                    className={styles.input}
                />
                <button onClick={addTodo} className={styles.addButton}>
                    추가
                </button>
            </div>
        </div>
    );
}

export default Todo;
