import React from 'react';

function Todo() {
    // 할 일 리스트 상태를 관리
    const [todos, setTodos] = useState([
        { id: 1, text: '친구 생일선물 사기', status: '진행중' },
        { id: 2, text: '과제하기', status: '진행중' },
        { id: 3, text: '노트 체크하기', status: '생성됨' },
        { id: 4, text: '쓰레기 버리기', status: '완료' }
    ]);

    // 새로운 할 일 입력을 관리하는 상태
    const [newTodo, setNewTodo] = useState('');

    // 할 일 추가 함수
    const addTodo = () => {
        if (newTodo.trim()) {
            const newTodoItem = {
                id: todos.length + 1,
                text: newTodo,
                status: '진행중'
            };
            setTodos([...todos, newTodoItem]);
            setNewTodo('');
        }
    };

    // 할 일 상태 업데이트 함수
    const updateStatus = (id) => {
        setTodos(todos.map(todo =>
            todo.id === id
                ? { ...todo, status: todo.status === '진행중' ? '완료' : '진행중' }
                : todo
        ));
    };

    return (
        <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
            <h2>할 일 목록</h2>
            <ul style={{ listStyleType: 'none', padding: 0 }}>
                {todos.map(todo => (
                    <li key={todo.id} style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        padding: '10px',
                        backgroundColor: '#f9f9f9',
                        marginBottom: '10px',
                        borderRadius: '4px'
                    }}>
                        <span>{todo.text}</span>
                        <button
                            onClick={() => updateStatus(todo.id)}
                            style={{
                                backgroundColor: todo.status === '완료' ? '#4caf50' : '#6200ea',
                                color: 'white',
                                border: 'none',
                                borderRadius: '12px',
                                padding: '5px 10px',
                                cursor: 'pointer'
                            }}
                        >
                            {todo.status}
                        </button>
                    </li>
                ))}
            </ul>
            <div style={{ display: 'flex', marginTop: '20px' }}>
                <input
                    type="text"
                    placeholder="할 일 추가하기..."
                    value={newTodo}
                    onChange={(e) => setNewTodo(e.target.value)}
                    style={{ flex: 1, padding: '10px', fontSize: '16px' }}
                />
                <button
                    onClick={addTodo}
                    style={{
                        padding: '10px',
                        fontSize: '16px',
                        backgroundColor: '#6200ea',
                        color: 'white',
                        border: 'none',
                        cursor: 'pointer'
                    }}
                >
                    추가
                </button>
            </div>
        </div>
    );
}

export default Todo;
